// üåü 2. COMPONENTE PRINCIPAL: Lista_productos
// -----------------------------------------------------------
export default function Lista_productos() {
    // 1. Estados para la lista de productos y manejo de la interfaz
    const [productos, setProductos] = useState([]); 
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [productoEditando, setProductoEditando] = useState(null); 

    // 2. Funci√≥n para cargar los datos de la base de datos (GET)
    const fetchProductos = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await fetch('http://localhost:5000/api/productos');
            
            if (response.ok) {
                const data = await response.json();
                setProductos(data); 
            } else {
                const errorData = await response.json();
                console.error('Error al cargar los productos:', response.status, errorData);
                setError('Error al cargar la lista: ' + (errorData.error || response.statusText));
            }
        } catch (err) {
            console.error('Error de red al obtener productos:', err);
            setError('Error de conexi√≥n con el servidor. Revisa que el backend est√© corriendo.');
        } finally {
            setLoading(false);
        }
    };

    // 3. useEffect: Llama a fetchProductos una vez al cargar el componente
    useEffect(() => {
        fetchProductos();
    }, []); 

    // üóëÔ∏è FUNCI√ìN ELIMINAR (DELETE)
    const handleDelete = async (id) => {
        if (!window.confirm(`¬øEst√°s seguro de que quieres eliminar el producto con ID ${id}? Esta acci√≥n es permanente.`)) {
            return; 
        }

        try {
            const response = await fetch(`http://localhost:5000/api/productos/${id}`, {
                method: 'DELETE',
            });

            if (response.ok) {
                setProductos(productos.filter(p => p.id_productos !== id));
                alert(`Producto ${id} eliminado con √©xito.`);
                
            } else {
                const errorData = await response.json();
                alert(`Error al eliminar: ${errorData.error || errorData.message}`);
            }
        } catch (err) {
            console.error('Error al conectar con la API para eliminar:', err);
            alert('Error de conexi√≥n con el servidor. Verifica el puerto 5000.');
        }
    };


    // ‚úèÔ∏è FUNCI√ìN PARA ABRIR EL MODAL (Reemplaza la alerta)
    const handleEdit = (id) => {
        const productoAEditar = productos.find(p => p.id_productos === id);
        setProductoEditando(productoAEditar); // Establece el producto para abrir el modal
    };

    // üîÑ FUNCI√ìN PARA CERRAR EL MODAL
    const handleCloseModal = () => {
        setProductoEditando(null); // Establece el producto a null para cerrar el modal
    };

    // üíæ FUNCI√ìN PARA GUARDAR CAMBIOS (PUT)
    const handleUpdateSubmit = async (productoActualizado) => {
        try {
            const response = await fetch(`http://localhost:5000/api/productos/${productoActualizado.id_productos}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(productoActualizado), 
            });

            if (response.ok) {
                // Actualizamos la lista mapeando y reemplazando el producto modificado
                setProductos(productos.map(p => 
                    p.id_productos === productoActualizado.id_productos ? productoActualizado : p
                ));

                handleCloseModal(); // Cerrar el modal despu√©s del √©xito
                alert(`Producto ${productoActualizado.id_productos} actualizado con √©xito.`);

            } else {
                const errorData = await response.json();
                alert(`Error al actualizar: ${errorData.error || errorData.message}. Verifique IDs de Categor√≠a/Proveedor.`);
            }
        } catch (err) {
            console.error('Error al conectar con la API para actualizar:', err);
            alert('Error de conexi√≥n con el servidor al intentar actualizar.');
        }
    };
    
    // 4. Renderizado 
    return (
        <div className="entrada-page">
            <header className="top-bar">
                <div className="top-left">
                    <div className="logo-wrap">
                        <div className="logo-circle">M</div>
                        <div className="brand">Mercapl√©n0</div>
                    </div>
                </div>
                <div className="top-right">
                    <button className="btn yellow outline">Cerrar sesi√≥n</button>
                </div>
            </header>

            <h1 className="page-title">Lista de Productos</h1>

            <main className="card-area">
                <section className="card list-card">
                    <div className="table-container">
                        
                        {/* Indicadores de estado */}
                        {loading && <p style={{ textAlign: 'center' }}>Cargando productos...</p>}
                        {error && <p className="error-message" style={{ color: 'red', textAlign: 'center' }}>{error}</p>}

                        {/* Tabla de Productos */}
                        {!loading && !error && (
                            <table className="product-table">
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Nombre</th>
                                        <th>Precio</th>
                                        <th>Categor√≠a</th>
                                        <th>Proveedor</th>
                                        <th>Descripci√≥n</th>
                                        <th>Estado</th>
                                        <th>Imagen</th>
                                        <th>Acciones</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {productos.length > 0 ? (
                                        productos.map((p) => (
                                            <tr key={p.id_productos}>
                                                <td>{p.id_productos}</td>
                                                <td>{p.nombre}</td>
                                                <td>${p.precio}</td>
                                                <td>{p.id_categoria}</td>
                                                <td>{p.id_proveedor}</td>
                                                <td>{p.descripcion}</td>
                                                <td>{p.estado}</td>
                                                <td className="img-cell">
                                                    <img 
                                                        src={p.imagen} 
                                                        alt={p.nombre} 
                                                        onError={(e) => {e.target.onerror = null; e.target.src="https://via.placeholder.com/50"}}
                                                    />
                                                </td>
                                                <td className="action-cell">
                                                    <button onClick={() => handleDelete(p.id_productos)} className="btn small red">
                                                        Eliminar
                                                    </button>
                                                    <button onClick={() => handleEdit(p.id_productos)} className="btn small yellow">
                                                        Editar
                                                    </button>
                                                </td>
                                            </tr>
                                        ))
                                    ) : (
                                        <tr>
                                            <td colSpan="9" style={{ textAlign: 'center' }}>No se encontraron productos.</td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        )}
                    </div>

                    <div className="card-footer">
                        <button className="btn green" onClick={() => window.history.back()}>Volver</button>
                    </div>
                </section>
            </main>

            {/* üí° RENDERIZADO DEL MODAL */}
            {productoEditando && (
                <ModalEdicion 
                    producto={productoEditando}
                    onCerrar={handleCloseModal}
                    onGuardar={handleUpdateSubmit}
                />
            )}
            
            <footer className="site-footer">
                <div className="footer-inner">
                    <div>¬© 2025 Portal 2 Todos los derechos reservados.</div>
                    <div className="socials">WhatsApp ¬∑ Facebook ¬∑ Instagram</div>
                </div>
            </footer>
        </div>
    )
}
